import React from "react";
const useDetailCommentData = () => {
  const [commentDataList, setCommentDataList] = React.useState([]);

  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState(false);
  React.useEffect(() => {
    const fetchData = async () => {
      try {
        const commentData = getCommentData();
        setCommentDataList(commentData);
        setLoading(false);
      } catch (error) {
        setError(true);
      }
    };
    fetchData();
  }, []);
  return [commentDataList, loading, error];
};

const getCommentData = () => {
  const index = 15;
  const content = {
    userImg: "/asset/user_img.jpg",
    userName: "괴루무루쥐",
    content: "내용이 이쓸까요 업쓸까요 ? ",
    like: 441231,
    dislike: 124,
    date: 123123,
  };
  const contentList = [];
  for (let i = 0; i < index; i++) {
    contentList.push(content);
  }
  return contentList;
};

export default useDetailCommentData;
