import React from "react";
const useCommentState = () => {
  const [commentState, setCommentState] = React.useState(false);
  const commentClickRef = React.useRef(null);
  const handleClickOutside = (type) => {
    // 클릭된 요소가 dialog 내부에 없으면
    if (
      commentClickRef.current &&
      !commentClickRef.current.contains(event.target)
    ) {
      setCommentState(type);
    }
  };
  const visibleClick = (event) => {
    event.stopPropagation();
  };
  const unVisibleClick = (event) => {
    console.log(event);
    event.stopPropagation();
    setCommentState(false);
  };
  return [commentClickRef, commentState, visibleClick, unVisibleClick];
};
export default useCommentState;
