import React from "react";
const useCommentState = () => {
  const [commentState, setCommentState] = React.useState(false);
  const commentRef = React.useRef();
  const visibleCommentState = (type, ref) => {
    setCommentState(type);
  };
  const visibleClick = (event) => {
    event.stopPropagation();
    setCommentState(true);
    console.log("Element:", commentRef.current); // DOM 요소에 접근
  };
  const unVisibleClick = (event) => {
    event.stopPropagation();
    setCommentState(false);
  };
  return [commentRef, commentState, visibleCommentState];
};
export default useCommentState;
