import {
  Container,
  UserBox,
  UserImgBox,
  UserImg,
  Box,
  UserText,
  TextBold,
  Text,
  Content,
  IconBox,
  Icon,
  IconText,
  SettingBox,
  SettingIcon,
} from "./style.js";
import { setDate, setNumber } from "../../../shared/setNumber/setNumber.js";

import BlackLikeIcon from "../../../shared/asset/like_white.js";
import BlackDislikeIcon from "../../../shared/asset/dislike_white.js";
import BlackSettingIcon from "../../../shared/asset/setting_white.js";

const Comment = (props) => {
  const { userImg, content, userName, like, dislike, date } = props;

  return (
    <Container>
      <UserBox>
        <UserImgBox>
          <UserImg src={userImg} />
        </UserImgBox>
        <Box>
          <UserText>
            <TextBold>{userName}</TextBold>
            <Text>{setDate(date)}</Text>
          </UserText>
          <Content>{content}</Content>
          <IconBox>
            <Icon src={BlackLikeIcon}></Icon>
            <Text>{setNumber(like)}</Text>
            <Icon src={BlackDislikeIcon}></Icon>
            <IconText>답글</IconText>
          </IconBox>
        </Box>
      </UserBox>
      <SettingBox>
        <SettingIcon src={BlackSettingIcon}></SettingIcon>
      </SettingBox>
    </Container>
  );
};

export default Comment;
